tinymce.PluginManager.add('qrcode',function(editor){function openmanager(){var title="Create QRcode";if(typeof tinymce.settings.qrcode_title!=="undefined"&&tinymce.settings.qrcode_title){title=tinymce.settingsqrcode_title;}
win=editor.windowManager.open({title:title,file:tinyMCE.baseURL+'/plugins/qrcode/qrcode.html',filetype:'image',width:650,height:510,inline:1,buttons:[{text:'cancel',onclick:function(){this.parent().parent().close();}}]});}
editor.addButton('qrcode',{icon:true,image:tinyMCE.baseURL+'/plugins/qrcode/icon.png',tooltip:'Create QRcode',shortcut:'Ctrl+M',onclick:openmanager});editor.addShortcut('Ctrl+M','',openmanager);editor.addMenuItem('qrcode',{icon:true,image:tinyMCE.baseURL+'/plugins/qrcode/icon.png',text:'Create QRcode',shortcut:'Ctrl+M',onclick:openmanager,context:'insert'});});
